mapper.select:
SELECT C.CODE_NAME ,B.BOARD_TYPE ,B.BOARD_NUM ,B.BOARD_TITLE ,B.BOARD_COMMENT ,B.TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN (
#{item}
)
) B INNER JOIN COM_CODE C ON B.BOARD_TYPE = C.CODE_ID WHERE NUMROW BETWEEN (#{pageNo}-1)*10 + 1 AND (#{pageNo})*10

mapper.insert:
        INSERT INTO 
        BOARD
        (BOARD_TYPE
        ,BOARD_NUM
        ,BOARD_TITLE
        ,BOARD_COMMENT
        ,CREATOR
        ,CREATE_TIME
        ,MODIFIER
        ,MODIFIED_TIME
        )
        VALUES
        (#{boardType}
        ,(SELECT NVL(TO_NUMBER(MAX(BOARD_NUM) + 1), 0)
	    FROM BOARD
	    WHERE BOARD_TYPE=#{boardType}
	    )
        ,#{boardTitle}
        ,#{boardComment}
        ,#{creator}
        ,TO_CHAR(SYSDATE)
        ,'SYSTEM'
        ,TO_CHAR(SYSDATE)
        )

mapper.delete:
DELETE
FROM BOARD
WHERE 
	BOARD_NUM = #{boardNum}
AND 
	BOARD_TYPE = #{boardType}
